///<summary>array utility. sort assoc array by key</summary>
/**
* array utility. sort assoc array by key
*/
function igk_array_sortbykey(& $tab){
    $k=array_keys($tab);
    $o=array();
    sort($k);
    foreach($k as $s=>$t){
        $o[$t]=$tab[$t];
    }
    $tab=$o;
    return $tab;
}
///<summary></summary>
///<param name="tab" ref="true"></param>
/**
* 
* @param mixed * $tab
*/
function igk_array_sortkey(& $tab){
    if(!is_array($tab))
        return false;
    $ckey=array_keys($tab);
    igk_usort($ckey, "igk_key_sort");
    $t=array();
    foreach($ckey as $k){
        $t[$k]=$tab[$k];
    }
    $tab=$t;
    return true;
}
///<summary>convert assoc-array to object presentation</summary>
/**
* convert assoc-array to object presentation
*/
function igk_array_to_obj($c, $ns){
    if(($c == null) || !is_array($c)){
        igk_assert_die(!igk_sys_env_production(), __FUNCTION__." Invalid argument");
        return null;
    }
    $t=array();
    $ln=strlen($ns) + 1;
    $ln -= $ns[$ln-2] == '/' ? 1: 0;
    foreach($c as $k=>$v){
        if(!strstr($k, $ns))
            continue;
        $n=substr($k, $ln);
        $tt=explode("/", $n);
        if(igk_count($tt) == 1){
            $t[$n]=$v;
        }
        else{
            $g=null;
            $nn=array_pop($tt);
            foreach($tt as $m){
                if(($g == null)){
                    if(isset($t[$m])){
                        $g=$t[$m];
                    }
                    else{
                        $g=igk_createobj();
                        $t[$m]=$g;
                    }
                }
                else{
                    if(!isset($g->$m)){
                        $g->$m=igk_createobj();
                        $g=$g->$m;
                    }
                }
            }
            $g->$nn=$v;
        }
    }
    return (object)$t;
}
