///<summary></summary>
///<param name="v"></param>
///<param name="$c"></param>
/**
* 
* @param mixed $v
* @param mixed $c
*/
function igk_cmp_array_value($v, $c){
    if(is_array($v) && is_array($c)){
        $dc=igk_count($v);
        $hc=igk_count($c);
        if($dc == $hc){
            for($i=0; $i < $dc; $i++){
                if($v[$i] !== $c[$i]){
                    return false;
                }
            }
            return true;
        }
    }
    return false;
}
///<summary></summary>
///<param name="o"></param>
///<param name="i"></param>
/**
* 
* @param mixed $o
* @param mixed $i
*/
function igk_cmp_refobj($o, $i){
    if(($o == null) && ($i == null))
        return true;
    if((($o == null) && ($i != null)) || (($o != null) && ($i == null)))
        return false;
    $cmp=igk_new_id(); 
    $o->$cmp=true; 
    $r=(!empty($i->$cmp));
    unset($o->$cmp);
    return $r;
}
///<summary>compare two version</summary>
///<exemple> 1.0.1 vs 1.0.5</exemple>
/**
* compare two version 
*/
function igk_cmp_version($v1, $v2){
    while(($tb1=explode(".", trim($v1))) && count($tb1) < 4){
        $v1 .= ".0";
    }
    while(($tb2=explode(".", trim($v2))) && count($tb2) < 4){
        $v2 .= ".0";
    }
    $c=igk_count($tb1);
    if($c == igk_count($tb2)){
        $i=0;
        while(($i < $c) && ($tb1[$i] === $tb2[$i])){
            $i++;
        }
        if($i < $c){
            if($tb1[$i] < $tb2[$i]){
                return -1;
            }
            return 1;
        }
    }
    return strcmp($v1, $v2);
}
