///<summary></summary>
///<param name="item"></param>
/**
* 
* @param mixed $item
*/
function igk_count($item){
    if(is_string($item))
        return strlen($item);
    if(is_array($item))
        return count($item);
    if(is_object($item)){
        if(method_exists(get_class($item), "getCount"))
            return $item->getCount();
        if(method_exists(get_class($item), "getRowCount"))
            return $item->getRowCount();
    }
    return 0;
}
