function igk_css_reg_mediaclass($th, $classname, $defaultvalue, $override=true){
    $igk=igk_app();
    if(($igk === null) || !$th || !IGKApp::IsInit())
        return;
    $s=$th[$classname];
    if(empty($s)){
        $th[$classname]=$defaultvalue;
