* @param mixed $value
*/
function igk_css_treatstyle($value, $theme= null, $systheme=null){
    $igk=igk_app();
    $theme = $theme ? $theme : $igk->Doc->Theme;
    if(empty($value) || !$igk || ($igk->Doc == null))
        return $value; 
    if ($systheme === null){
        $systheme = $igk->Doc->getSysTheme();
    } 
    return igk_css_treat_gtheme($theme, $systheme, $value);
}
