///<summary> used to dump query result</summary>
/**
*  used to dump query result
*/
function igk_db_dump_result($result){
    $r=igk_createnode();
    if($result === null){
        $r->Content="No result";
    }
    else{
        $r->addDiv()->Content="Count : ".$result->RowCount;
        $tb=$r->addTable();
        $tr=$tb->addTr();
        foreach($result->Columns as $v){
            $td=$tr->add("th");
            $td->Content=$v->name;
        }
        foreach($result->Rows as  $v){
            $tr=$tb->addTr();
            foreach($v as  $t){
                $tr->addTd()->Content=$t;
            }
        }
    }
    $r->RenderAJX();
}
