    }
    $schema=igk_db_backup_ctrl($ctrl);
    $dataxml=($schema) ? $schema->Render(): null;
    $ctrl->resetDb(0);
    if($dataxml){
        $error=[];
        igk_db_restore_backup_data($ctrl, $dataxml, $error);
    }
    $file=$ctrl->getDataDir()."/dbbackup/".date("YmdHis").".db.bck.xml";
    igk_io_w2file($file, $dataxml);
}
