///<summary>return an array of default sys ignored folder keys</summary>
/**
* return an array of default sys ignored folder keys
*/
function igk_default_ignore_lib($dir=null){
    $tk=array(
            IGK_LIB_FOLDER=>1,
            IGK_CONF_FOLDER=> 1, 
            IGK_DATA_FOLDER=>1,
            IGK_VIEW_FOLDER=>1,
            IGK_CONTENT_FOLDER=>1,
            IGK_SCRIPT_FOLDER=>1
        );
    if($dir){
        $keys=array_keys($tk);
        foreach($keys as $m){
            $tk[igk_html_uri($dir.'/'.$m)
            ]=1;
        }
    }
    return $tk;
}
