function igk_display($obj, $keyTab=null){
    if($keyTab){
        $c=igk_get_env("sys://tabdisplay/".$keyTab);
        if($c){
            if(is_callable($c)){
                return $c($obj);
            }
            else{
                igk_wln("no display bind");
