*/
function igk_display_error($a){
    if($a){
        ini_set("display_errors", "1");
        switch(igk_server()->ENVIRONMENT){
            case "development":
                error_reporting(-1);
                ini_set('display_errors', 1);
            break;
            default: 
                error_reporting(E_ALL | E_STRICT | E_NOTICE);
                ini_set('error_reporting', E_ALL | E_STRICT);
