///<summary>dump value</summary>
/**
* dump value
*/
function igk_dump($v){
    $callers=debug_backtrace();
    $from=igk_getv($callers, 0);
    $d=igk_createnode('div');
    $d["class"]="igk-dump";
    $l=$d->add('span');
    $l->add("i")->Content=$from["file"];
    $l->addText(':');
    $l->add("b")->Content=$from["line"];
    if(is_object($v)){
        $st="{";
        $h=0;
        foreach($v as $k=>$s){
            if($h){
                $st .= ', <br />';
            }
            $st .= "<font color='red'>\"{$k}\"</font>=&gt;";
            if(is_object($s)){
                $st .= 'Object T';
            }
            else if(is_array($s)){
                $st .= 'Array T';
            }
            else{
                $st .= $s;
            }
            $h=1;
        }
        $st .= "}";
        $d->addDiv()->Content=$st;
    }
    else if(is_array($v)){
        $st="[";
        $h=0;
        foreach($v as $k=>$s){
            if($h){
                $st .= '</span>, <br /> ';
            }
            $st .= "<span><font color='red'>\"{$k}\"</font>=&gt;";
            if(is_object($s)){
                $st .= 'Object T';
            }
            else if(is_array($s)){
                $st .= 'Array T';
            }
            else{
                $st .= $s;
            }
            $h=1;
        }
        $st .= '</span>]';
        $d->addDiv()->Content=$st;
    }
    return $d->Render();
}
///<summary> dump array</summary>
/**
*  dump array
*/
function igk_dump_array($tab){
    $n=igk_createnode('div');
    $n["class"]="dumparray igk-row";
    foreach($tab as $k=>$v){
        $r=$n->addDiv();
        $r->addSpan()->setClass("k")->Content=$k;
        $r->addSpan()->setClass("v")->Content=is_string($v) ? $v: (is_object($v) ? get_class($v): $v);
