    $frame->Form->addHSep();
    $frame->Form->addInput("confirm", "hidden", 1);
    $frame->Form->addInput("frame-id", "hidden", $id);
    $frame->Form->addInput("frame-close-uri", "hidden", igk_getctrl(IGK_FRAME_CTRL)->getUri("closeFrame_ajx&navigate=false&id=".$id));
