///<summary>retrieve environment variable as object</summary>
/**
* retrieve environment variable as object
*/
function igk_get_env_obj($ns){
    $c=igk_get_env_all($ns);
    if(!$c)
        return null;
    $t=array();
    $ln=strlen($ns) + 1;
    $ln -= $ns[$ln-2] == '/' ? 1: 0;
    foreach($c as $k=>$v){
        $n=substr($k, $ln);
        $tt=explode("/", $n);
        if(igk_count($tt) == 1){
            $t[$n]=$v;
        }
        else{
            $g=null;
            $nn=array_pop($tt);
            foreach($tt as $m){
                if(($g == null)){
                    if(isset($t[$m])){
                        $g=$t[$m];
                    }
                    else{
                        $g=igk_createobj();
                        $t[$m]=$g;
                    }
                }
                else{
                    if(!isset($g->$m)){
                        $g->$m=igk_createobj();
                        $g=$g->$m;
                    }
                }
            }
            $g->$nn=$v;
        }
    }
    return (object)$t;
}
