    if($value == null)
        return null;
    if(is_string($value))
        return IGKHtmlItem::GetStringAttribute($value, null);
    switch($attr){
        case "Ctrl":
        return IGKHtmlItem::GetStringAttribute($value->getName(), null);

    }
