* param mixed 
*/
function igk_getrs(){
    if( ($c = func_num_args()) > 0){
        $tab=array();
        $tm = func_get_args();
        if (($c==1 ) && is_array($m = func_get_arg(0))){
            $tm = $m;
        }
        foreach($m as $v){
            $t=igk_getr($v);
            $tab[$v]=$t;
        }        
        return $tab;
    }
    return $_REQUEST;
}
