function igk_getv_fallback($item, $keys){
    if (is_string($keys)){
        $keys = explode("|", $keys);
    }
    foreach($keys as $r){
        if (! ($m = igk_getv($item, $r))){
            return $m;
        }
    }
    return null;
}
