///<summary>specifu cache output</summary>
/**
* specifu cache output
*/
function igk_header_cache_output($second=3600){
    $ts=gmdate("D, d M Y H:i:s", time() + $second). " GMT";
    header("Expires: {$ts}");
    header("Pragma: cache");
    header("Cache-Control: max-age={$second}, public");
}
