*/
function igk_header_set_contenttype($type, $charset="charset=utf-8"){
    $data="";
    $mime=igk_header_mime();
    if($charset)
        $charset=";".$charset;
    $data=igk_getv($mime, $type, IGK_CT_PLAIN_TEXT).$charset;
    header("Content-Type: ".$data);
}
