* @param mixed * $form
* @param mixed $closed the default value is false
*/
function igk_html_frame_ex($ctrl, $id, $title, $uri, & $form, $closed=false){
    $frame=igk_html_frame($ctrl, $id);
    $div=$frame->BoxContent;
    $div->ClearChilds();
    $frame->Title=$title;
    $frm=$div->addForm();
    $frm["action"]=$uri;
