///<summary>get real index of node in parent</summary>
/**
* get real index of node in parent
*/
function igk_html_index_of($node){
    $p=$node->getParentNode();
    if($p == null)
        return null;
    $i=0;
    foreach($p->Childs as $v){
        if($v === $node)
            return $i;
        $i++;
    }
    return $i;
}
