    $f=igk_io_currentrelativepath($path);
    if(!empty($f) && file_exists($f)){
        $r=igk_realpath($f);
        $s=igk_io_basepath($f);
        if($s == $r){
            $m=igk_html_uri(igk_io_baseuri()."/".$f);
