///unscape text area content
/**
*/
function igk_html_unscape($out){
    $reg="/(?P<name>([\\][\"\'\\\\]))/i";
    $c=preg_match_all($reg, $out, $t);
    for($i=0; $i < $c; $i++){
        switch($t["name"][$i]){
            case '\\"':
            $out=str_replace('\\"', '"', $out);
            break;
            case "\\'":
            $out=str_replace("\\'", '\'', $out);
            break;
            case '\\':
            $out=str_replace('\\', "\\", $out);
            break;
        }
    }
    return $out;
}
