///<summary>copy stream function</summary>
/**
* copy stream function
*/
function igk_io_copy_stream($in, $out, $buffer=4096, $close=0){
    $size=0;
    while(!feof($in)){
        $size += fwrite($out, fread($in, $buffer));
    }
    if($close){
        fclose($in);
        fclose($out);
    }
    return $size;
}
