function igk_io_scandir($dir){
    if (!is_dir($dir)){
        return [];
    }
    $tab = array_filter( scandir($dir) , function($v){
        return (($v!=".") && ($v!=".."));
    });
    if (!$tab ) $tab = [];
    $tab = array_map(function($d)use($dir){
        return $dir.DIRECTORY_SEPARATOR.$d;
    }, $tab);
    return $tab;
}
