///<summary>check if is sub directory</summary>
/**
* check if is sub directory
*/
function igk_io_is_subdir($p, $c){
    if(DIRECTORY_SEPARATOR != "/"){
        $p=str_replace("\\", "/", $p);
        $c=str_replace("\\", "/", $c);
    }
    if(empty($p)){
        igk_die(__FUNCTION__."::p is empty ");
    }
    if(empty($c)){
        igk_die(__FUNCTION__."::c is empty");
    }
    return (strpos($c, $p) === 0);
}
