///<summary></summary>
///<param name="f"></param>
///<param name="endpattern"></param>
/**
* 
* @param mixed $f
* @param mixed $endpattern
*/
function igk_io_read_header($f, $endpattern){
    if(!file_exists($f))
        return false;
    $hf=fopen($f, "r");
    $s=IGK_STR_EMPTY;
    while(($c=fread($hf, 1)) !== null){
        $s .= $c;
        if(preg_match($endpattern, $s))
            break;
    }
    fclose($hf);
    return $s;
}
