* 
* @param mixed $txt
*/
function igk_io_remove_bom($txt){
    if((strlen($txt) > 3) && (ord($txt[0]) === 239) && (ord($txt[1]) === 187) && (ord($txt[2]) === 191)){
        return substr($txt, 3);
    }
    return $txt;
}
