/**
* get request uri entry according to base dir
*/
function igk_io_request_entry(){
    $b=igk_io_request_uri();
    $t=igk_html_uri(dirname((($g=igk_server()->SCRIPT_NAME) ? $g: igk_server()->PHP_SELF)));
    $s=$b;
    if(strstr($b, $t)){
        $s="/".ltrim(substr($b, strlen($t)), "/");
    }
    return urldecode($s);
}
