*/
function igk_is_callable($tab){
    if($tab == null)
        return 0;
    if(is_callable($tab))
        return true;
    if(is_array($tab) && count($tab) > 2){
        $c=array_slice($tab, 0, 2);
        return is_callable($c);
    }
    return igk_is_callback_obj($tab);
}
