function igk_js_inline_text($msg, $attrib=1){
	$msg = str_replace("\\", "\\\\", $msg);
	$msg = str_replace("'", "\\'", $msg);
	if ($attrib){
		$msg = str_replace('"','\\&quot;', $msg);
	}
	return "['".implode("','", explode("\n", $msg))."'].join(String.fromCharCode(10))";
}

