*/
function igk_js_post_frame_cmd($uri, $ctrl=null, $global=true){
    $q=IGK_STR_EMPTY;
    if($ctrl != null){
        if(is_string($ctrl)){
            $q=",'".$ctrl."'";
        }
        else
            $q=",'".$ctrl->TargetNode["id"]."'";
    }
    else
        $q=",null";
    if($global)
        $q .= ",true";
    else
        $q .= ",false";
