///<summary>utility function used to split string</summary>
/**
* utility function used to split string
*/
function igk_mail_split_str($s, $ln=75, $sep=IGK_LF){
    $n="";
    while(!empty($s)){
        if(!empty($n))
            $n .= $sep;
        $h=substr($s, 0, $ln);
        $s=substr($s, strlen($h));
        $n .= $h;
    }
    return $n;
}
