*/
function igk_push_article_chain($f, $context=null){ 
    $key = "sys://article_chain"; 
    $ctx = $context;
    $b = igk_get_env($key);
    if (!$b || (count($b) == 0)){
        if (is_array($ctx->raw)){
            if (array_key_exists("raw", $ctx->raw)){
