*/
function igk_run_scripts($files){
    $files=is_array($files) ? implode(" ", $files): $files;
    $v_rp=igk_get_run_script_path();
    $cmd=$v_rp." ".igk_io_dir(IGK_LIB_RUNFILE)." ".$files;
    $out=[];
    exec($cmd, $out, $ret);
    if($ret == 0){}
    else{
        igk_ilog("failed to run > ".$cmd);
    }
    return implode(IGK_LF, $out);
}
