            "startLine"=>0,
            "endLine"=>-1,
            "removeComment"=>1,
            "ignoreEmptyLine"=>1,
            "FormatText"=>1,
            "@context"=>"",
            "ignoreProcessor"=>1,
            "noHandleDefinitions"=>0,
            "noAutoParameter"=>0,
            "indentChar"=>"\t",
            "endOffset"=>0,
            "stop"=>0,
            "data"=>"",
            "isDebug"=>0,
            "mode"=>0,
            "treatClassStatement"=>0,
            "offsetBracket"=>0,
            "noPhpPreprocessor"=>0,
            "definitions"=>null,
            "documentation"=>null,
