function igk_sys_add_cache_uri($muri, $file=null){
    $f=IGK_APP_DIR."/Caches/uri.cache";
    $t=array();
    if(file_exists($f)){
        include($f);
    }
    $d=igk_date_now();
    if(is_array($muri)){
        foreach($muri as $k=>$v){
            $t[$k]=$v."|".$d;
