    $defctrl=$ctrl ?? igk_get_defaultwebpagectrl();
    if($defctrl && ($ownctrl || igk_own_view_ctrl($defctrl)) && method_exists($defctrl, "is_handle_uri") && $defctrl->is_handle_uri($uri)){
