/**
* 
* @param mixed $scheme the default value is 'http'
*/
function igk_sys_srv_uri_scheme($scheme='http'){
    $r=igk_server()->REQUEST_SCHEME;
    if($r)
        return $r;
    if(preg_match_all("/^(?<scheme>(.)+):\/\//i", igk_server()->SCRIPT_URI, $tab))
