///<param name="b"></param>
/**
* 
* @param mixed $b
*/
function igk_typeof($b){
    if($b === null)
        return "null";
    if(is_string($b))
        return "string";
    if(is_object($b)){
        return get_class($b);
    }
    if(is_array($b))
        return "array";
    return "unknow";
}
