///<summary></summary>
///<param name="name"></param>
///<param name="callback"></param>
/**
* 
* @param mixed $name
* @param mixed $callback
*/
function igk_unreg_hook($name, $callback){
    $hooks=igk_environment()->{"sys://hooks"};
    if(!$hooks){
        return 0;
    }
    $tb=& $hooks[$name]->list;
    foreach($tb as $k=>$v){
        if($v->callback === $callback){
            unset($tb[$k]);
            return 1;
        }
    }
    return 0;
}
