*/
function igk_uri_add_args($u, $args=null){
    $u=parse_url($u);
    $q=$args ? http_build_query($args): null;
    $o=$u["path"];
    if(isset($u["query"]))
        $o .= "?".$u["query"].($q ? "&".$q: null);
    else if(!empty($q)){
        $o .= "?".$q;
    }
    return $o;
}
