///<summary>generate password</summary>
/**
* generate password
*/
function igk_user_genpwd($length=8){
    static $chars=null;
    if(!$chars)
        $chars="abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789@#!()_";
    $r="";
    $count=strlen($chars);
    for($i=0; $i < $length; $i++){
        $idx=rand(0, $count - 1);
        $r .= substr($chars, $idx, 1);
    }
    return $r;
}
