    * @param mixed $files
    */
    public function setFiles($files){

        if(($files == null) || !is_string($files)){
            unset($this->_[self::FILES_RULE]);
        }
        else{
            $this->_[self::FILES_RULE]=igk_io_collapse_path($files);
        }
    }
    ///<summary></summary>
    ///<param name="seri"></param>
    /**
    * 
