///<summary>represent event properties</summary>
/**
* represent event properties
*/
class IGKHtmlEventProperty implements IIGKHtmlGetValue , ArrayAccess {
    private $_n;
    protected $_p;
    ///<summary></summary>
    ///<param name="name"></param>
    /**
    * 
    * @param mixed $name
    */
    protected function __construct($name){
        $this->_n=$name;
        $this->_p=[];
    }
    ///<summary></summary>
    ///<param name="n"></param>
    /**
    * 
    * @param mixed $n
    */
    public function __get($n){
        return igk_getv($this->_p, $n);
    }
    ///<summary></summary>
    ///<param name="n"></param>
    ///<param name="v"></param>
    /**
    * 
    * @param mixed $n
    * @param mixed $v
    * @return void
    */
    public function __set($n, $v){
        switch(strtolower($n)){
            case '@__callback':
            $this->$n=$v;
            return;
            case 'content':
            $this->_p=$v;
            return;
        }
        if($v == null){
            unset($this->_p[$n]);
        }
        $this->_p[$n]=$v;
    }
    ///<summary>display value</summary>
    /**
    * display value
    */
    public function __toString(){
        return get_class($this). " : ".$this->_n;
    }
    ///<summary></summary>
    ///<param name="name"></param>
    /**
    * 
    * @param mixed $name
    */
    public static function CreateEventProperty($name){
        return new IGKHtmlEventProperty($name);
    }
    ///<summary></summary>
    /**
    * 
    */
    public function getid(){
        return $this->_n;
    }
    ///<summary>override this to get vavlue</summary>
    /**
    * override this to get vavlue
    */
    public function getValue($options=null){
        $s="";
        if(is_string($this->_p)){
            $s=$this->_p;
        }
        else{
            foreach($this->_p as $k=>$v){
                $s .= $k."=".$v.";";
            }
        }
        if(empty($s))
            return "";
        return "[".$this->_n. "]=\"".IGKHtmlUtils::GetAttributeValue($s)."\"";
    }
    ///<summary></summary>
    ///<param name="i"></param>
    /**
    * 
    * @param mixed $i
    */
    public function offsetExists($i){
        return false;
    }
    ///<summary></summary>
    ///<param name="i"></param>
    /**
    * 
    * @param mixed $i
    */
    public function offsetGet($i){
        $n='@__callback';
        if(isset($this->$n)){
            $fc=$this->$n;
            unset($this->$n);
            return $fc($this, $i);
        }
        return $this->_p;
    }
    ///<summary></summary>
    ///<param name="i"></param>
    ///<param name="v"></param>
    /**
    * 
    * @param mixed $i
    * @param mixed $v
    */
    public function offsetSet($i, $v){
        $this->_n=$i;
        $this->_p=$v;
        $n='@__callback';
        if(isset($this->$n)){
            $fc=$this->$n;
            unset($this->$n);
            $fc($this);
        }
    }
    ///<summary></summary>
    ///<param name="i"></param>
    /**
    * 
    * @param mixed $i
    */
    public function offsetUnset($i){
        $this->_p=[];
    }
    ///<summary></summary>
    ///<param name="id"></param>
    /**
    * 
    * @param mixed $id
    */
    public function setid($id){
        $this->_n=$id;
    }
}
