    ///<summary></summary>
    ///<param name="f"></param>
    /**
    * 
    * @param mixed $f
    */
    public function IsFunctionExposed($f){
        $v=false;
        if(method_exists(get_Class($this), $f)){
            $b=new ReflectionMethod(get_class($this), $f);
            $v=$b->isPublic();
        }
        if($v && igk_is_conf_connected()){
            return $v;
        }
            return $v || (strtolower($f) == "connecttoconfig");

    }
