            $h=array("controller");
            foreach($h as $k=>$v){
                $r=$k + 1;
                $v_tabc->addTabPage(__("tab.".$v), $this->getUri("ca_tabv_ajx&g={$r}"), 0);
            }
            $v_tabc->setTabViewListener(null);
            $v_tabc->select(0);
