        $d=urldecode(base64_decode(igk_getr("d")));
        $_dir=$this->_getPageInfo()->m_directory;
        $file=igk_io_currentrelativepath($_dir."/".$d);
        if(igk_qr_confirm()){
            if(is_file($file)){
                unlink($file);
                $this->View();
