        $t=array();
        $c=igk_db_table_select_where(igk_db_get_table_name(IGK_TB_AUTHORISATIONS), null, $this);
        if($c && $c->Success){
            foreach($c->Rows as  $v){
                if($u->IsAuthorize($v->clName)){
