    /**
    * 
    */
    public function group_dropgroup_ajx(){
        $id=igk_getr(IGK_FD_ID);
        if(igk_qr_confirm()){
            if($this->_removeGroup($id)){
                igk_notifyctrl()->addMsgr("msg.group.removed");
            }
            $this->View();
            $this->TargetNode->RenderAJX();
