    protected static function initDb(){
        $f=igk_io_syspath(IGK_MENU_CONF_DATA);
        if(file_exists($f) == false){
            $content=<<<EOF
DEFAULT,0,,,default,1
EOF;
            igk_io_save_file_as_utf8($f, $content, true);
            $this->_ReLoadMenu();
            return 1;
        } 
        return 0;
    }
